Model.sklearn SGDClassifier:
Pipeline (
  StandardScaler (
    with_std=True
  ),
  SKL2RiverClassifier (
    estimator=SGDClassifier(eta0=0.005, learning_rate='constant', loss='log_loss',
                penalty=None)
    classes=[False, True]
  )
)

Model.River MLP:
Pipeline (
  StandardScaler (
    with_std=True
  ),
  MLPRegressor (
    hidden_dims=(5,)
    activations=(<class 'river.neural_net.activations.ReLU'>, <class 'river.neural_net.activations.ReLU'>, <class 'river.neural_net.activations.Identity'>)
    loss=Squared ()
    optimizer=SGD (
      lr=Constant (
        learning_rate=0.001
      )
    )
    seed=42
  )
)
